// Button Base Styles
.qk-btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  border: 1px solid transparent;
  border-radius: 0.375rem;
  font-family: inherit;
  font-weight: 500;
  text-align: center;
  text-decoration: none;
  vertical-align: middle;
  cursor: pointer;
  user-select: none;
  transition: all 0.15s ease-in-out;
  position: relative;
  overflow: hidden;

  &:focus {
    outline: 2px solid transparent;
    outline-offset: 2px;
    box-shadow: 0 0 0 2px rgba(59, 130, 246, 0.5);
  }

  &:disabled,
  &--disabled {
    pointer-events: none;
    opacity: 0.6;
    cursor: not-allowed;
  }

  // Button Variants
  &--primary {
    background-color: #3b82f6;
    border-color: #3b82f6;
    color: #ffffff;

    &:hover:not(:disabled) {
      background-color: #2563eb;
      border-color: #2563eb;
    }

    &:active:not(:disabled) {
      background-color: #1d4ed8;
      border-color: #1d4ed8;
    }
  }

  &--secondary {
    background-color: #6b7280;
    border-color: #6b7280;
    color: #ffffff;

    &:hover:not(:disabled) {
      background-color: #4b5563;
      border-color: #4b5563;
    }

    &:active:not(:disabled) {
      background-color: #374151;
      border-color: #374151;
    }
  }

  &--success {
    background-color: #10b981;
    border-color: #10b981;
    color: #ffffff;

    &:hover:not(:disabled) {
      background-color: #059669;
      border-color: #059669;
    }

    &:active:not(:disabled) {
      background-color: #047857;
      border-color: #047857;
    }
  }

  &--danger {
    background-color: #ef4444;
    border-color: #ef4444;
    color: #ffffff;

    &:hover:not(:disabled) {
      background-color: #dc2626;
      border-color: #dc2626;
    }

    &:active:not(:disabled) {
      background-color: #b91c1c;
      border-color: #b91c1c;
    }
  }

  &--warning {
    background-color: #f59e0b;
    border-color: #f59e0b;
    color: #ffffff;

    &:hover:not(:disabled) {
      background-color: #d97706;
      border-color: #d97706;
    }

    &:active:not(:disabled) {
      background-color: #b45309;
      border-color: #b45309;
    }
  }

  &--info {
    background-color: #06b6d4;
    border-color: #06b6d4;
    color: #ffffff;

    &:hover:not(:disabled) {
      background-color: #0891b2;
      border-color: #0891b2;
    }

    &:active:not(:disabled) {
      background-color: #0e7490;
      border-color: #0e7490;
    }
  }

  &--light {
    background-color: #f8fafc;
    border-color: #e2e8f0;
    color: #1e293b;

    &:hover:not(:disabled) {
      background-color: #f1f5f9;
      border-color: #cbd5e1;
    }

    &:active:not(:disabled) {
      background-color: #e2e8f0;
      border-color: #94a3b8;
    }
  }

  &--dark {
    background-color: #1e293b;
    border-color: #1e293b;
    color: #ffffff;

    &:hover:not(:disabled) {
      background-color: #0f172a;
      border-color: #0f172a;
    }

    &:active:not(:disabled) {
      background-color: #020617;
      border-color: #020617;
    }
  }

  // Outline Variants
  &--outline-primary {
    background-color: transparent;
    border-color: #3b82f6;
    color: #3b82f6;

    &:hover:not(:disabled) {
      background-color: #3b82f6;
      color: #ffffff;
    }
  }

  &--outline-secondary {
    background-color: transparent;
    border-color: #6b7280;
    color: #6b7280;

    &:hover:not(:disabled) {
      background-color: #6b7280;
      color: #ffffff;
    }
  }

  &--outline-success {
    background-color: transparent;
    border-color: #10b981;
    color: #10b981;

    &:hover:not(:disabled) {
      background-color: #10b981;
      color: #ffffff;
    }
  }

  &--outline-danger {
    background-color: transparent;
    border-color: #ef4444;
    color: #ef4444;

    &:hover:not(:disabled) {
      background-color: #ef4444;
      color: #ffffff;
    }
  }

  &--outline-warning {
    background-color: transparent;
    border-color: #f59e0b;
    color: #f59e0b;

    &:hover:not(:disabled) {
      background-color: #f59e0b;
      color: #ffffff;
    }
  }

  &--outline-info {
    background-color: transparent;
    border-color: #06b6d4;
    color: #06b6d4;

    &:hover:not(:disabled) {
      background-color: #06b6d4;
      color: #ffffff;
    }
  }

  &--outline-light {
    background-color: transparent;
    border-color: #e2e8f0;
    color: #64748b;

    &:hover:not(:disabled) {
      background-color: #f8fafc;
      border-color: #cbd5e1;
    }
  }

  &--outline-dark {
    background-color: transparent;
    border-color: #1e293b;
    color: #1e293b;

    &:hover:not(:disabled) {
      background-color: #1e293b;
      color: #ffffff;
    }
  }

  // Button Sizes
  &--xs {
    padding: 0.375rem 0.75rem;
    font-size: 0.75rem;
    line-height: 1rem;
  }

  &--sm {
    padding: 0.5rem 1rem;
    font-size: 0.875rem;
    line-height: 1.25rem;
  }

  &--md {
    padding: 0.625rem 1.25rem;
    font-size: 0.875rem;
    line-height: 1.25rem;
  }

  &--lg {
    padding: 0.75rem 1.5rem;
    font-size: 1rem;
    line-height: 1.5rem;
  }

  &--xl {
    padding: 0.875rem 1.75rem;
    font-size: 1.125rem;
    line-height: 1.75rem;
  }

  // Button Modifiers
  &--full-width {
    width: 100%;
  }

  &--rounded {
    border-radius: 9999px;
  }

  &--square {
    border-radius: 0;
  }

  &--loading {
    pointer-events: none;
  }
}

// Button Content
.qk-btn-content {
  transition: opacity 0.15s ease-in-out;

  &--hidden {
    opacity: 0;
  }
}

// Loading Spinner
.qk-btn-spinner {
  display: inline-block;
  width: 1rem;
  height: 1rem;
  border: 2px solid transparent;
  border-top: 2px solid currentColor;
  border-radius: 50%;
  animation: qk-btn-spin 1s linear infinite;
}

@keyframes qk-btn-spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}

// Focus visible for better accessibility
.qk-btn:focus-visible {
  outline: 2px solid transparent;
  outline-offset: 2px;
  box-shadow: 0 0 0 2px rgba(59, 130, 246, 0.5);
}

// High contrast mode support
@media (prefers-contrast: high) {
  .qk-btn {
    border-width: 2px;
  }
}

// Reduced motion support
@media (prefers-reduced-motion: reduce) {
  .qk-btn {
    transition: none;
  }
  
  .qk-btn-spinner {
    animation: none;
  }
}

