// Material 3 Tooltip Component Styles
// Based on Material Design 3 specifications

.qk-tooltip-trigger {
  display: inline-block;
  cursor: help;

  &:focus {
    outline: 2px solid var(--md-sys-color-primary);
    outline-offset: 2px;
    border-radius: 4px;
  }

  &:focus:not(:focus-visible) {
    outline: none;
  }

  // When containing interactive elements, don't apply interactive styles to wrapper
  &:not([role="button"]) {
    cursor: default;
    
    &:focus {
      outline: none;
    }
  }
}

.qk-tooltip {
  position: fixed;
  z-index: 1000;
  pointer-events: auto;
  transition: opacity 0.15s ease-in-out, transform 0.15s ease-in-out;
  opacity: 1;
  transform: scale(1);
  filter: drop-shadow(0 1px 2px rgba(0, 0, 0, 0.3)) drop-shadow(0 1px 3px rgba(0, 0, 0, 0.15));

  // Material 3 elevation tokens
  box-shadow: 
    0px 1px 2px 0px rgba(0, 0, 0, 0.3),
    0px 1px 3px 1px rgba(0, 0, 0, 0.15);

  &--plain {
    background: var(--md-sys-color-inverse-surface, #313033);
    color: var(--md-sys-color-inverse-on-surface, #f4eff4);
    border-radius: 4px;
    max-width: 320px;
    padding: 4px 8px;
    font-family: var(--md-sys-typescale-body-small-font, 'Roboto', sans-serif);
    font-size: var(--md-sys-typescale-body-small-size, 12px);
    font-weight: var(--md-sys-typescale-body-small-weight, 400);
    line-height: var(--md-sys-typescale-body-small-line-height, 16px);
    letter-spacing: var(--md-sys-typescale-body-small-tracking, 0.4px);
  }

  &--rich {
    background: var(--md-sys-color-surface-container, #f3edf7);
    color: var(--md-sys-color-on-surface, #1d1b20);
    border-radius: 12px;
    max-width: 320px;
    padding: 16px;
    font-family: var(--md-sys-typescale-body-medium-font, 'Roboto', sans-serif);
    font-size: var(--md-sys-typescale-body-medium-size, 14px);
    font-weight: var(--md-sys-typescale-body-medium-weight, 400);
    line-height: var(--md-sys-typescale-body-medium-line-height, 20px);
    letter-spacing: var(--md-sys-typescale-body-medium-tracking, 0.25px);
    border: 1px solid var(--md-sys-color-outline-variant, #cac4d0);
  }

  &--persistent {
    pointer-events: auto;
    cursor: default;

    &:hover {
      opacity: 1;
    }
  }

  // Position-specific spacing adjustments
  &--top,
  &--top-start,
  &--top-end {
    margin-bottom: 8px;
  }

  &--bottom,
  &--bottom-start,
  &--bottom-end {
    margin-top: 8px;
  }

  &--left,
  &--left-start,
  &--left-end {
    margin-right: 8px;
  }

  &--right,
  &--right-start,
  &--right-end {
    margin-left: 8px;
  }
}

.qk-tooltip-text {
  word-wrap: break-word;
  hyphens: auto;
}

.qk-tooltip-rich {
  word-wrap: break-word;
  hyphens: auto;

  // Rich tooltip content styling
  h1, h2, h3, h4, h5, h6 {
    margin: 0 0 8px 0;
    font-weight: var(--md-sys-typescale-title-small-weight, 500);
  }

  p {
    margin: 0 0 8px 0;

    &:last-child {
      margin-bottom: 0;
    }
  }

  ul, ol {
    margin: 0 0 8px 0;
    padding-left: 16px;
  }

  li {
    margin-bottom: 4px;

    &:last-child {
      margin-bottom: 0;
    }
  }

  a {
    color: var(--md-sys-color-primary, #6750a4);
    text-decoration: underline;

    &:hover {
      color: var(--md-sys-color-primary, #6750a4);
      text-decoration: none;
    }

    &:focus {
      outline: 2px solid var(--md-sys-color-primary, #6750a4);
      outline-offset: 2px;
      border-radius: 2px;
    }
  }

  code {
    background: var(--md-sys-color-surface-variant, #e7e0ec);
    color: var(--md-sys-color-on-surface-variant, #49454f);
    padding: 2px 4px;
    border-radius: 4px;
    font-family: 'Roboto Mono', 'Courier New', monospace;
    font-size: 12px;
  }
}

.qk-tooltip-arrow {
  position: absolute;
  width: 0;
  height: 0;
  pointer-events: none;

  // Plain tooltip arrow
  .qk-tooltip--plain & {
    border-style: solid;
    border-color: transparent;

    &--top {
      bottom: -4px;
      left: 50%;
      transform: translateX(-50%);
      border-width: 4px 4px 0 4px;
      border-top-color: var(--md-sys-color-inverse-surface, #313033);
    }

    &--top-start {
      bottom: -4px;
      left: 12px;
      border-width: 4px 4px 0 4px;
      border-top-color: var(--md-sys-color-inverse-surface, #313033);
    }

    &--top-end {
      bottom: -4px;
      right: 12px;
      border-width: 4px 4px 0 4px;
      border-top-color: var(--md-sys-color-inverse-surface, #313033);
    }

    &--bottom {
      top: -4px;
      left: 50%;
      transform: translateX(-50%);
      border-width: 0 4px 4px 4px;
      border-bottom-color: var(--md-sys-color-inverse-surface, #313033);
    }

    &--bottom-start {
      top: -4px;
      left: 12px;
      border-width: 0 4px 4px 4px;
      border-bottom-color: var(--md-sys-color-inverse-surface, #313033);
    }

    &--bottom-end {
      top: -4px;
      right: 12px;
      border-width: 0 4px 4px 4px;
      border-bottom-color: var(--md-sys-color-inverse-surface, #313033);
    }

    &--left {
      right: -4px;
      top: 50%;
      transform: translateY(-50%);
      border-width: 4px 0 4px 4px;
      border-left-color: var(--md-sys-color-inverse-surface, #313033);
    }

    &--left-start {
      right: -4px;
      top: 12px;
      border-width: 4px 0 4px 4px;
      border-left-color: var(--md-sys-color-inverse-surface, #313033);
    }

    &--left-end {
      right: -4px;
      bottom: 12px;
      border-width: 4px 0 4px 4px;
      border-left-color: var(--md-sys-color-inverse-surface, #313033);
    }

    &--right {
      left: -4px;
      top: 50%;
      transform: translateY(-50%);
      border-width: 4px 4px 4px 0;
      border-right-color: var(--md-sys-color-inverse-surface, #313033);
    }

    &--right-start {
      left: -4px;
      top: 12px;
      border-width: 4px 4px 4px 0;
      border-right-color: var(--md-sys-color-inverse-surface, #313033);
    }

    &--right-end {
      left: -4px;
      bottom: 12px;
      border-width: 4px 4px 4px 0;
      border-right-color: var(--md-sys-color-inverse-surface, #313033);
    }
  }

  // Rich tooltip arrow (smaller, different color)
  .qk-tooltip--rich & {
    border-style: solid;
    border-color: transparent;

    &--top {
      bottom: -6px;
      left: 50%;
      transform: translateX(-50%);
      border-width: 6px 6px 0 6px;
      border-top-color: var(--md-sys-color-outline-variant, #cac4d0);
    }

    &--top-start {
      bottom: -6px;
      left: 16px;
      border-width: 6px 6px 0 6px;
      border-top-color: var(--md-sys-color-outline-variant, #cac4d0);
    }

    &--top-end {
      bottom: -6px;
      right: 16px;
      border-width: 6px 6px 0 6px;
      border-top-color: var(--md-sys-color-outline-variant, #cac4d0);
    }

    &--bottom {
      top: -6px;
      left: 50%;
      transform: translateX(-50%);
      border-width: 0 6px 6px 6px;
      border-bottom-color: var(--md-sys-color-outline-variant, #cac4d0);
    }

    &--bottom-start {
      top: -6px;
      left: 16px;
      border-width: 0 6px 6px 6px;
      border-bottom-color: var(--md-sys-color-outline-variant, #cac4d0);
    }

    &--bottom-end {
      top: -6px;
      right: 16px;
      border-width: 0 6px 6px 6px;
      border-bottom-color: var(--md-sys-color-outline-variant, #cac4d0);
    }

    &--left {
      right: -6px;
      top: 50%;
      transform: translateY(-50%);
      border-width: 6px 0 6px 6px;
      border-left-color: var(--md-sys-color-outline-variant, #cac4d0);
    }

    &--left-start {
      right: -6px;
      top: 16px;
      border-width: 6px 0 6px 6px;
      border-left-color: var(--md-sys-color-outline-variant, #cac4d0);
    }

    &--left-end {
      right: -6px;
      bottom: 16px;
      border-width: 6px 0 6px 6px;
      border-left-color: var(--md-sys-color-outline-variant, #cac4d0);
    }

    &--right {
      left: -6px;
      top: 50%;
      transform: translateY(-50%);
      border-width: 6px 6px 6px 0;
      border-right-color: var(--md-sys-color-outline-variant, #cac4d0);
    }

    &--right-start {
      left: -6px;
      top: 16px;
      border-width: 6px 6px 6px 0;
      border-right-color: var(--md-sys-color-outline-variant, #cac4d0);
    }

    &--right-end {
      left: -6px;
      bottom: 16px;
      border-width: 6px 6px 6px 0;
      border-right-color: var(--md-sys-color-outline-variant, #cac4d0);
    }
  }
}

// Animation states
.qk-tooltip {
  &[aria-hidden="true"] {
    opacity: 0;
    transform: scale(0.8);
    pointer-events: none;
  }
}

// High contrast mode support
@media (prefers-contrast: high) {
  .qk-tooltip--plain {
    border: 2px solid var(--md-sys-color-outline, #79747e);
  }

  .qk-tooltip--rich {
    border: 2px solid var(--md-sys-color-outline, #79747e);
  }
}

// Reduced motion support
@media (prefers-reduced-motion: reduce) {
  .qk-tooltip {
    transition: none;
  }
}

// Dark theme support
@media (prefers-color-scheme: dark) {
  .qk-tooltip--plain {
    background: var(--md-sys-color-inverse-surface, #f4eff4);
    color: var(--md-sys-color-inverse-on-surface, #313033);
  }

  .qk-tooltip--rich {
    background: var(--md-sys-color-surface-container, #211f26);
    color: var(--md-sys-color-on-surface, #e6e0e9);
    border-color: var(--md-sys-color-outline-variant, #4a4458);
  }
}

